package otros;

public class ContenedorCromosomas {
	private boolean[] cromosomaA;
	private boolean[] cromosomaB;
	
	public ContenedorCromosomas(boolean[] pA, boolean[] pB) {
		// TODO Auto-generated constructor stub
		cromosomaA = pA;
		cromosomaB = pB;
	}

	public boolean[] getCromosomaA() {
		return cromosomaA;
	}

	public boolean[] getCromosomaB() {
		return cromosomaB;
	}
	
}
